import React, { Component } from "react";
import "materialize-css/dist/css/materialize.min.css";
import Section from './section'
import './css/home.css'
import Line from "./Charts/chart";
import { createData } from "../actions/postActions"
import { postColumns } from "../actions/postColumns"
import { connect } from "react-redux"

class UserPage extends Component {

    callApi(){
        fetch('http://localhost:9000/API/read/')
        .then(res => res.text())
        .then(res => this.props.createData(res))
    }

    columns(){
        fetch('http://localhost:9000/API/read/columns')
        .then(res => res.text())
        .then(res => {
            let col = []
            res.map((x)=>{
                col.push(x.column_name)
            })
            this.props.postColumns(col)
        })
    }

    create(data){
        this.props.createData(data)
    }

    componentDidMount(){
        this.callApi()
    }



    render(){
        return(
            [
            <Section/>,
            <Line/>
            ]
        );
    };

}

const mapDispatchToProps = (dispatch) => {
    return{
        createData: (data) => {dispatch(createData(data))},
        postColumns: (col) => {dispatch(postColumns(col))}
    }
}

export default connect(null, mapDispatchToProps)(UserPage);
