import AChart from "react-apexcharts";
const op = (id, name) =>{
    return(    
        {options: {chart: {
        //id: id,
        type: 'area',
        group: 'social',
        stacked: false,
        height: '100%',
        width:"100%",
        animations: {
            enabled: true,
            easing: 'easeinout',
            speed: 800,
            animateGradually: {
                enabled: true,
                delay: 150
            },
            dynamicAnimation: {
                enabled: true,
                speed: 350
            },
            initialAnimation: {
                enabled: true
            }
        },
        zoom: {
            type: 'x',
            enabled: true,
            autoScaleYaxis: true
        },
        toolbar: {
            autoSelected: 'zoom'
        },
    },
    markers: {
        size: 0,
    },
    xaxis: {
        categories: [],
        type: "datetime",
        labels: {
            datetimeFormatter: {
                year: 'yyyy',
                month: 'MMM \'yy',
                day: 'dd MMM',
                hour: 'HH:mm'
            }
        }
    },
    fill: {
        colors: undefined,
        opacity: 0.9,
        type: 'solid',
        gradient: {
            shade: 'dark',
            type: "horizontal",
            shadeIntensity: 0.5,
            gradientToColors: undefined,
            inverseColors: true,
            opacityFrom: 1,
            opacityTo: 1,
            stops: [0, 50, 100],
            colorStops: []
        }
    },
    dataLabels: {
        enabled: false
    },
    title: {
        //text: name,
        align: "center",
        margin: 20,
        offsetY: 20,
        style: {
            fontSize: "25px"
        }
    },
    yaxis: {
        labels: {
            minWidth: 40
        }
    }}
        }
    )
};

const Seeds= ({todos, options}) =>{
    const todoList = todos.length ? (
        todos.map( (todo, index) => {
            let option = 'chart-'.concat(index);
            let name= todo.name;
            let parameters = op(option, name);
            console.log(parameters);
            console.log(options);
            return(
                <div className="col s6" key={index}>
                    <div className="card">
                        <AChart
                        options={parameters}
                        series={[todo]}/>
                    </div>
                </div>
            );
        }
        )
    ) : (
        <p className="center">Loading</p>
    );
    return(
        <div className="container">
            <div className="row">
                {todoList}
            </div>
        </div>
    );
}
export default Seeds;
