    ]
}

const rootReducer = (state=initState, action) =>{
    if (action.type === 'DELETE_POST') {
        let newPost = state.posts.filter(post => {
            return action.id !== post.id
        })
        return {
            ...state,
            posts: newPost
        }
    }
    return state;
}


export default rootReducer;
