const initState= {
    data: [],
    col: [],
    series: [],
    options: {
        chart: {
        height: 450,
        width: "100%",
        type: "bar",
        background: "#FFFFFF",
        foreColor: "#333"
        },
        xaxis: {
        categories: [],
        type: ""
        },
        fill: {
        type: "gradient",
        gradient: {
            shadeIntensity: 1,
            inverseColors: false,
            opacityFrom: 0.5,
            opacityTo: 0,
            stops: [0, 90, 100]
        }
        },
        dataLabels: {
        enabled: false
        },
        title: {
        text: "Equalizer",
        align: "center",
        margin: 20,
        offsetY: 20,
        style: {
            fontSize: "25px"
        }
        }
    },
}

const rootReducer = (state=initState, action) =>{
    if (action.type === 'CREATE_DATA') {
        return {
            ...state,
            data: action.data
        }
    }

    if (action.type === 'POST_COLUMNS') {
        return {
            ...state,
            col: action.col
        }
    }

    if (action.type === 'POST_DATE') {
        return {
            ...state,
            options: action.options
        }
    }

    if (action.type === 'POST_LINES') {
        return {
            ...state,
            series: action.series
        }
    }



    return state;
}


export default rootReducer;
